mapper.select:
SELECT * from  tiekuai.t_integral_product t  WHERE t.is_deleted = 0
 AND t.product_name LIKE #{name} 
 AND t.status = #{status} 
 AND t.product_type = #{cate} 

ORDER BY t.create_time DESC

mapper.update:
UPDATE tiekuai.t_integral_product SET status = #{status}
WHERE id in

    #{item}

mapper.select:
       SELECT *
        FROM
	t_integral_order t where 1=1
         AND t.order_name LIKE #{name} 
         AND t.order_state = #{status} 
         AND t.order_type = #{cate}
