mapper.update:
UPDATE t_shuffling SET is_deleted = 1
WHERE is_deleted=0 AND id in

    #{item}

UPDATE t_shuffling SET status = #{status}
WHERE id in

    #{item}

mapper.select:
SELECT
   *
FROM
    t_shuffling
WHERE
    is_deleted = 0 and cate=#{cate}
 AND shuffling_name LIKE #{name} 
 AND status = #{status}
