mapper.select:
SELECT id,code,name,DATE_FORMAT(create_time , '%Y-%m-%d %h:%i:%s') AS create_time FROM sys_role
 WHERE is_delete = 0
 AND name LIKE #{name}

mapper.update:
UPDATE sys_role
   SET name = #{name} ,
       update_by = #{update_by} ,
       update_time = #{update_time}
 WHERE id = #{id}
  AND is_delete = 0

UPDATE sys_role
   SET is_delete = 1,
       update_by = #{update_by},
       update_time = #{update_time}
 WHERE id in

    #{item}
