mapper.update:
UPDATE t_user
   SET
       integration_val  = #{integration_val},
       achievement_val  = #{achievement_val},
       badge_count  = #{badge_count}
WHERE  open_id      = #{open_id}

UPDATE t_user
SET
integration_val  = #{integration_val}
WHERE  open_id      = #{open_id}

UPDATE t_user_address
   SET is_default = 0
 WHERE open_id = #{open_id}

UPDATE t_user_address
   SET is_default = 1
 WHERE id = #{id}
   AND open_id = #{open_id}
